app/drizzle/schema/best-practices.schema.contents:
// @ts-nocheck
import { pgTable, text, integer, boolean, timestamp, json, jsonb, varchar, char, numeric, time, date, pgEnum } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Best practice categories enum
export const practiceTypeEnum = pgEnum("practice_type", ["workflow", "agent", "tool_usage", "configuration", "security", "performance"]);
export const practiceStatusEnum = pgEnum("practice_status", ["active", "deprecated", "experimental"]);

// Best practices table
export const bestPracticesTable = pgTable("best_practices", {
  id: integer("id").primaryKey().generatedAlwaysAsIdentity(),
  title: text("title").notNull(),
  description: text("description").notNull(),
  category: text("category").notNull(),
  type: practiceTypeEnum("type").notNull(),
  status: practiceStatusEnum("status").default("active").notNull(),
  tags: text("tags").array(),
  content: text("content").notNull(),
  examples: json("examples").$type<Array<{
    title: string;
    description: string;
    code?: string;
    configuration?: Record<string, any>;
  }>>(),
  relatedTools: text("related_tools").array(),
  difficulty: text("difficulty").notNull(), // beginner, intermediate, advanced
  estimatedTime: text("estimated_time"), // e.g., "5 minutes", "1 hour"
  benefits: text("benefits").array(),
  commonMistakes: text("common_mistakes").array(),
  metadata: json("metadata").$type<Record<string, any>>(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Practice recommendations table
export const practiceRecommendationsTable = pgTable("practice_recommendations", {
  id: integer("id").primaryKey().generatedAlwaysAsIdentity(),
  sessionId: text("session_id"),
  userInput: text("user_input").notNull(),
  taskType: text("task_type").notNull(),
  selectedTools: text("selected_tools").array(),
  recommendedPractices: json("recommended_practices").$type<Array<{
    practiceId: number;
    relevanceScore: number;
    reasoning: string;
    priority: "high" | "medium" | "low";
  }>>(),
  analysisResults: json("analysis_results").$type<{
    complexity: string;
    riskLevel: string;
    suggestedApproach: string;
    timeEstimate: string;
    requiredSkills: string[];
    potentialChallenges: string[];
  }>(),
  customRecommendations: text("custom_recommendations").array(),
  confidence: integer("confidence"), // 0-100
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Validation schemas
export const insertBestPracticeSchema = createInsertSchema(bestPracticesTable).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPracticeRecommendationSchema = createInsertSchema(practiceRecommendationsTable).omit({
  id: true,
  createdAt: true,
});

// TypeScript types
export type BestPractice = typeof bestPracticesTable.$inferSelect;
export type InsertBestPractice = z.infer<typeof insertBestPracticeSchema>;
export type PracticeRecommendation = typeof practiceRecommendationsTable.$inferSelect;
export type InsertPracticeRecommendation = z.infer<typeof insertPracticeRecommendationSchema>;

// Extended types for joined data
export type BestPracticeWithRecommendations = BestPractice & {
  recommendationCount: number;
  lastUsed?: Date;
};

export type RecommendationWithPractices = PracticeRecommendation & {
  practices: BestPractice[];
};

app/drizzle/schema/schema.server.ts.contents:
// @ts-nocheck
import { pgTable, text, integer, boolean, timestamp, json, jsonb, varchar, char, numeric, time, date, pgEnum } from "drizzle-orm/pg-core";
import { createInsertSchema, createSelectSchema } from "drizzle-zod";
import { z } from "zod";

// MANDATORY CORE TABLES - Always include these
export const usersTable = pgTable("users", {
  id: integer().primaryKey().generatedAlwaysAsIdentity(),
  email: text().notNull().unique(),
  password: text().notNull(),
});

export const stripeCustomersTable = pgTable("stripe_customers", {
  userId: integer().primaryKey(),
  customerId: text().notNull(),
});

// Agent/Workflow Configuration Tables
export const agentTypesEnum = pgEnum("agent_types", ["agent", "workflow"]);
export const agentStatusEnum = pgEnum("agent_status", ["draft", "active", "archived"]);

export const agentsTable = pgTable("agents", {
  id: integer().primaryKey().generatedAlwaysAsIdentity(),
  name: text().notNull(),
  description: text().notNull(),
  type: agentTypesEnum().notNull(),
  status: agentStatusEnum().default("draft").notNull(),
  taskRequirements: text().notNull(),
  generatedPrompt: text(),
  configuration: json().$type<{
    tools: string[];
    parameters: Record<string, any>;
    workflow_steps?: Array<{
      step: string;
      description: string;
      tools: string[];
    }>;
    best_practices?: string[];
    recommendations?: string[];
  }>(),
  createdAt: timestamp().defaultNow().notNull(),
  updatedAt: timestamp().defaultNow().notNull(),
});

export const toolsTable = pgTable("tools", {
  id: integer().primaryKey().generatedAlwaysAsIdentity(),
  name: text().notNull().unique(),
  description: text().notNull(),
  category: text().notNull(),
  apiEndpoint: text(),
  parameters: json().$type<Array<{
    name: string;
    type: string;
    required: boolean;
    description: string;
    defaultValue?: any;
  }>>(),
  isActive: boolean().default(true).notNull(),
  createdAt: timestamp().defaultNow().notNull(),
});

export const agentToolsTable = pgTable("agent_tools", {
  id: integer().primaryKey().generatedAlwaysAsIdentity(),
  agentId: integer().notNull(),
  toolId: integer().notNull(),
  configuration: json().$type<Record<string, any>>(),
  createdAt: timestamp().defaultNow().notNull(),
});

export const executionLogsTable = pgTable("execution_logs", {
  id: integer().primaryKey().generatedAlwaysAsIdentity(),
  agentId: integer().notNull(),
  command: text().notNull(),
  parameters: json().$type<Record<string, any>>(),
  result: text(),
  status: text().notNull(),
  executionTime: integer(), // milliseconds
  createdAt: timestamp().defaultNow().notNull(),
});

// Export user session schemas
export * from "./user-sessions.schema.server";

// Export best practices schemas
export * from "./best-practices.schema.server";

// Validation schemas
export const insertAgentSchema = createInsertSchema(agentsTable).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertToolSchema = createInsertSchema(toolsTable).omit({
  id: true,
  createdAt: true,
});

export const insertExecutionLogSchema = createInsertSchema(executionLogsTable).omit({
  id: true,
  createdAt: true,
});

// TypeScript types
export type Agent = typeof agentsTable.$inferSelect;
export type InsertAgent = z.infer<typeof insertAgentSchema>;
export type Tool = typeof toolsTable.$inferSelect;
export type InsertTool = z.infer<typeof insertToolSchema>;
export type AgentTool = typeof agentToolsTable.$inferSelect;
export type ExecutionLog = typeof executionLogsTable.$inferSelect;
export type InsertExecutionLog = z.infer<typeof insertExecutionLogSchema>;

// Extended types for joined data
export type AgentWithTools = Agent & {
  tools: (Tool & { configuration?: Record<string, any> })[];
};

export type AgentWithLogs = Agent & {
  executionLogs: ExecutionLog[];
  lastExecution?: ExecutionLog;
};

