// Types for our CLI application
interface PromptTemplate {
  id: string;
  name: string;
  description: string;
  category: string;
  template: string;
  parameters: PromptParameter[];
  createdAt: string;
}

interface PromptParameter {
  name: string;
  type: 'string' | 'number' | 'boolean' | 'select';
  description: string;
  required: boolean;
  defaultValue?: any;
  options?: string[];
}

interface CLICommand {
  command: string;
  description: string;
  example: string;
  category: string;
}

interface Agent {
  id: number;
  name: string;
  description: string;
  type: "agent" | "workflow";
  status: "draft" | "active" | "archived";
  taskRequirements: string;
  generatedPrompt?: string;
  configuration?: any;
  createdAt: Date;
  updatedAt: Date;
}

interface Tool {
  id: number;
  name: string;
  description: string;
  category: string;
  apiEndpoint?: string;
  parameters?: any[];
  isActive: boolean;
  createdAt: Date;
}

interface UserSession {
  id: number;
  sessionId: string;
  currentStep: string;
  status: string;
  context?: any;
  createdAt: Date;
  updatedAt: Date;
}

interface ProcessingResult {
  response: string;
  nextStep: string;
  missingInfo: string[];
  recommendations: string[];
  confidence: number;
  suggestions: string[];
  isComplete: boolean;
  followUpQuestions?: string[];
}

interface BestPractice {
  id: number;
  title: string;
  description: string;
  category: string;
  type: string;
  status: string;
  tags: string[];
  content: string;
  examples?: any[];
  relatedTools: string[];
  difficulty: string;
  estimatedTime: string;
  benefits: string[];
  commonMistakes: string[];
  createdAt: Date;
  updatedAt: Date;
}

interface BestPracticeAnalysis {
  taskComplexity: "simple" | "moderate" | "complex";
  riskLevel: "low" | "medium" | "high";
  suggestedApproach: string;
  timeEstimate: string;
  requiredSkills: string[];
  potentialChallenges: string[];
  recommendedPractices: Array<{
    title: string;
    priority: "high" | "medium" | "low";
    reasoning: string;
    category: string;
  }>;
  customRecommendations: string[];
  confidence: number;
}

