import { execSync } from "child_process";
import { fileURLToPath } from "url";
import { dirname, join } from "path";

import { db } from "../app/drizzle/config.server";
import { sql } from "drizzle-orm";

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const projectRoot = join(__dirname, "..");

function runCommand(command) {
  try {
    const output = execSync(command, {
      cwd: projectRoot,
      stdio: "pipe",
      encoding: "utf8",
    });
    return { success: true, output };
  } catch (error) {
    return {
      success: false,
      output: error.stdout?.toString() || error.message,
    };
  }
}

async function main() {
  console.log("Running initial database migration...");

  try {
    await reset();
  } catch (error) {
    console.error("âŒ Reset failed");
    console.error(error);
  }

  const firstAttempt = runCommand("pnpm drizzle-kit push");

  if (
    !firstAttempt.success ||
    firstAttempt.output.includes("ASSERTIONS") ||
    firstAttempt.output.includes("created or renamed")
  ) {
    console.log(
      "Migration failed or contains assertions error. Cleaning up and retrying..."
    );

    try {
      await reset();
    } catch (error) {
      console.error("âŒ Reset failed second time");
      console.error(error);
    }

    console.log("Running migration again...");

    const secondAttempt = runCommand("pnpm drizzle-kit push");

    if (!secondAttempt.success) {
      console.error("Migration failed after cleanup:", secondAttempt.output);
      process.exit(1);
    }
  }

  console.log("Database migration completed");
}

main().catch((error) => {
  console.error("Script failed:", error);
  process.exit(1);
});

async function reset() {
  console.log("â³ Resetting database...");

  const start = Date.now();

  const query = sql`
      -- Delete all tables
      DO $$ DECLARE
          r RECORD;
      BEGIN
          FOR r IN (SELECT tablename FROM pg_tables WHERE schemaname = current_schema()) LOOP
              EXECUTE 'DROP TABLE IF EXISTS ' || quote_ident(r.tablename) || ' CASCADE';
          END LOOP;
      END $$;

      -- Delete enums
      DO $$ DECLARE
          r RECORD;
      BEGIN
          FOR r IN (select t.typname as enum_name
          from pg_type t 
              join pg_enum e on t.oid = e.enumtypid  
              join pg_catalog.pg_namespace n ON n.oid = t.typnamespace
          where n.nspname = current_schema()) LOOP
              EXECUTE 'DROP TYPE IF EXISTS ' || quote_ident(r.enum_name);
          END LOOP;
      END $$;

      `;

  await db.execute(query);

  const end = Date.now();
  console.log(`âœ… Reset end & took ${end - start}ms`);
  console.log("");
}

scripts/duplicate-table.ts.contents:
import * as fs from 'fs';
import * as path from 'path';
import * as ts from 'typescript';
import * as glob from 'glob';

// Interface to represent a table definition
interface TableDefinition {
  variableName: string;
  tableName: string | null;
  filePath: string;
}

// Function to find all table definitions
function findTableDefinitions(): TableDefinition[] {
  // Use hardcoded schema path
  const schemaPathPattern = 'app/drizzle/schema/*';
  const schemaFiles = glob.sync(schemaPathPattern);

  const tables: TableDefinition[] = [];

  for (const file of schemaFiles) {
    const content = fs.readFileSync(file, 'utf-8');
    const sourceFile = ts.createSourceFile(
      file,
      content,
      ts.ScriptTarget.Latest,
      true
    );

    function visit(node: ts.Node) {
      // Look for variable declarations
      if (
        ts.isVariableDeclaration(node) && 
        ts.isIdentifier(node.name) && 
        node.initializer
      ) {
        const variableName = node.name.text;
        const initializer = node.initializer;

        // Check if it's a table definition (a call to pgTable, sqliteTable, etc.)
        if (ts.isCallExpression(initializer)) {
          const functionName = initializer.expression.getText(sourceFile);

          if (
            functionName === 'pgTable' ||
            functionName === 'mysqlTable' ||
            functionName === 'sqliteTable' ||
            functionName.endsWith('.table') ||
            functionName === 'createTable'
          ) {
            let sqlTableName: string | null = null;

            // Extract SQL table name if present
            if (
              initializer.arguments.length > 0 &&
              ts.isStringLiteral(initializer.arguments[0])
            ) {
              sqlTableName = initializer.arguments[0].text;
            }

            tables.push({
              variableName,
              tableName: sqlTableName,
              filePath: file
            });
          }
        }
      }

      ts.forEachChild(node, visit);
    }

    visit(sourceFile);
  }

  return tables;
}

// Main function to check for duplicate tables
function checkDuplicateTables(): void {
  const tables = findTableDefinitions();

  if (tables.length === 0) {
    return;
  }

  // Check for duplicate variable names
  const variableNameMap = new Map<string, TableDefinition[]>();
  for (const table of tables) {
    if (!variableNameMap.has(table.variableName)) {
      variableNameMap.set(table.variableName, []);
    }
    variableNameMap.get(table.variableName)!.push(table);
  }

  // Check for duplicate SQL table names
  const sqlTableNameMap = new Map<string, TableDefinition[]>();
  for (const table of tables) {
    if (table.tableName) {
      if (!sqlTableNameMap.has(table.tableName)) {
        sqlTableNameMap.set(table.tableName, []);
      }
      sqlTableNameMap.get(table.tableName)!.push(table);
    }
  }

  let hasDuplicates = false;

  // Report duplicate variable names
  for (const [name, definitions] of variableNameMap.entries()) {
    if (definitions.length > 1) {
      hasDuplicates = true;
      console.log(`âš ï¸  Duplicate variable name: '${name}'`);
      console.log('   Defined in:');
      definitions.forEach(def => {
        console.log(`   - ${path.relative(process.cwd(), def.filePath)}`);
      });
    }
  }

  // Report duplicate SQL table names
  for (const [name, definitions] of sqlTableNameMap.entries()) {
    if (definitions.length > 1) {
      hasDuplicates = true;
      console.log(`âš ï¸  Duplicate SQL table name: '${name}'`);
      console.log('   Defined as:');
      definitions.forEach(def => {
        console.log(`   - ${def.variableName} in ${path.relative(process.cwd(), def.filePath)}`);
      });
    }
  }

  if (hasDuplicates) {
    console.error('âŒ Found duplicate table definitions. Remove duplicate tables. Don\'t rename them.');
    process.exit(1);
  }
  // No output if everything is fine - silently exit with code 0
}

// Run the script
checkDuplicateTables();

scripts/generate-migration.ts.contents:
import pty from 'node-pty';

function runDrizzleGenerateWithPty(): Promise<void> {
  return new Promise((resolve, reject) => {
    console.log('ðŸš€ Running drizzle-kit generate...');

    const ptyProcess = pty.spawn('pnpm', ['drizzle-kit', 'generate'], {
      name: 'xterm-color',
      cols: 80,
      rows: 30,
      cwd: process.cwd(),
      env: process.env,
    });

    let completed = false;

    const timeout = setTimeout(() => {
      if (!completed) {
        console.error('\nâ° Timeout: drizzle-kit did not complete in 30 seconds. Aborting...');
        ptyProcess.kill();
        reject(new Error('Process timeout after 30 seconds'));
      }
    }, 30_000);

    ptyProcess.onData((data) => {
      process.stdout.write(data);
      // default behavior is column create, which is want we want
      ptyProcess.write('\r');
    });

    ptyProcess.onExit(({ exitCode }) => {
      completed = true;
      clearTimeout(timeout);

      if (exitCode === 0) {
        console.log('ðŸŽ‰ drizzle-kit completed successfully.');
        resolve();
      } else {
        reject(new Error(`âŒ drizzle-kit exited with code ${exitCode}`));
      }
    });
  });
}



async function runWithRetry(maxRetries: number = 3): Promise<void> {
  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      console.log(`\nðŸ“ Attempt migration generation ${attempt}/${maxRetries}`);
      await runDrizzleGenerateWithPty();
      return;
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : String(error);
      console.error(`\nâŒ Attempt migration generation ${attempt} failed:`, errorMessage);

      if (attempt === maxRetries) {
        console.error('\nðŸ’¥ All migration generation attempts failed. Exiting...');
        process.exit(1);
      } else {
        console.log(`\nðŸ”„ Retrying migration generation in 2 seconds...`);
        await new Promise(resolve => setTimeout(resolve, 2000));
      }
    }
  }
}

// Run if this file is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  await runWithRetry();
}

